import { useState } from "react";
import "./Display.css";

const Display = ({ contract, account }) => {
  const [data, setData] = useState([]);

  // Fetch and display files
  const getdata = async () => {
    let dataArray;
    const Otheraddress = document.querySelector(".address").value;

    try {
      if (Otheraddress) {
        dataArray = await contract.display(Otheraddress);
      } else {
        dataArray = await contract.display(account);
        console.log("Data from contract:", dataArray);
      }
    } catch (e) {
      alert("You don't have access");
      console.error(e);
      return;
    }

    const isEmpty = !dataArray || dataArray.length === 0;

    if (!isEmpty) {
      const str_array = dataArray.toString().split(",");
      console.log("Processed Data Array:", str_array);

      const images = str_array.map((item) => ({
        url: `https://gateway.pinata.cloud/ipfs/${item.replace("ipfs://", "")}`,
        cid: item.replace("ipfs://", ""), // Assuming CID is used to identify the file
      }));

      setData(images);
    } else {
      alert("No images to display");
      setData([]);
    }
  };

  // Delete a file
  const deleteFile = async (cid) => {
    try {
      // Call the smart contract function to delete the file (assuming the function is `deleteFile`)
      await contract.deleteFile(cid, { from: account });
      alert("File deleted successfully!");

      // Update local state to remove the deleted file
      setData((prevData) => prevData.filter((file) => file.cid !== cid));
    } catch (e) {
      console.error("Error deleting file:", e);
      alert("Failed to delete the file.");
    }
  };

  return (
    <>
      <div className="image-list">
        {data.length > 0 ? (
          data.map((file, i) => (
            <div key={i} className="file-item">
              <a href={file.url} target="_blank" rel="noopener noreferrer">
                <img
                  src={file.url}
                  alt={`Image ${i + 1}`}
                  className="image-list"
                  onError={(e) => (e.target.style.display = "none")} // Hide broken images
                />
              </a>
              <button
                className="delete-button"
                onClick={() => deleteFile(file.cid)}
              >
                Delete
              </button>
            </div>
          ))
        ) : (
          <p>No images found</p>
        )}
      </div>
      <input
        type="text"
        placeholder="Enter Address"
        className="address"
      />
      <button className="center button" onClick={getdata}>
        Get Data
      </button>
    </>
  );
};

export default Display;
