import { useState } from "react";
import "./Display.css";
const Display = ({ contract, account }) => {
  const [data, setData] = useState("");
  const getdata = async () => {
    let dataArray;
    const Otheraddress = document.querySelector(".address").value;
    try {
      if (Otheraddress) {
        dataArray = await contract.display(Otheraddress);
        
      } else {
        dataArray = await contract.display(account);
        console.log(dataArray);
      }
    } catch (e) {
      alert("You don't have access");
    }
    const isEmpty = Object.keys(dataArray).length === 0;

    if (!isEmpty) {
      const str = dataArray.toString();
      const str_array = str.split(",");
      // console.log(str);
      // console.log(str_array);
      const images = str_array.map((item, i) => {
        return (
          <a href={item} key={i} target="_blank">
            <img
              key={i}
              src={`https://gateway.pinata.cloud/ipfs/${item.substring(6)}`}
              alt="new"
              className="image-list"
            ></img>
          </a>
        );
      });
      setData(images);
    } else {
      alert("No image to display");
    }
  };

   // Delete a file
   const deleteFile = async (cid) => {
    try {
      // Call the smart contract function to delete the file (assuming the function is `deleteFile`)
      await contract.deleteFile(cid, { from: account });
      alert("File deleted successfully!");

      // Update local state to remove the deleted file
      setData((prevData) => prevData.filter((file) => file.cid !== cid));
    } catch (e) {
      console.error("Error deleting file:", e);
      alert("Failed to delete the file.");
    }
  };
  
  return (
    <>
      <div className="image-list">{data}</div>
      <input
        type="text"
        placeholder="Enter Address"
        className="address"
      ></input>
      <button className="center button" onClick={getdata}>
        Get Data
      </button>
    </>
  );
};
export default Display;
